mapper.select:
SELECT t1.xTime, t1.taskCount, t2.qualifiedCount, ROUND(t2.qualifiedCount / t1.taskCount,2) passRate FROM ( WITH t AS ( SELECT ROWNUM - 1 xTime FROM dual CONNECT BY ROWNUM <= 24 ), t1 AS( select count(1) taskCount, TO_CHAR(DETECT_END_DATE,'hh24') hour from MIDHD.MT_DETECT_TASK_INFO
IS_DEL = 0 AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') = TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'hh24') ) SELECT xTime, taskCount FROM t LEFT JOIN t1 ON t1.hour = t.xTime ORDER BY xTime ASC )t1 LEFT JOIN ( SELECT count(1) qualifiedCount, TO_CHAR(DETECT_END_DATE,'hh24') xTime FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') = TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd') AND DET_CONC_CODE = 1
AND EQUIP_CATEG = #{dto.equipCateg}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'hh24') )t2 ON t1.xTime = t2.xTime

SELECT t1.xTime, t1.taskCount, t2.qualifiedCount, ROUND(t2.qualifiedCount / t1.taskCount,2) passRate FROM ( select count(1) taskCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') xTime from MIDHD.MT_DETECT_TASK_INFO
IS_DEL = 0 AND TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') BETWEEN to_char(#{dto.startDealTime},'yyyy/mm/dd') AND to_char(#{dto.endDealTime},'yyyy/mm/dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') )t1 LEFT JOIN ( SELECT count(1) qualifiedCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') xTime FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND DET_CONC_CODE = 1 AND TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') BETWEEN to_char(#{dto.startDealTime},'yyyy/mm/dd') AND to_char(#{dto.endDealTime},'yyyy/mm/dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') )t2 ON t1.xTime = t2.xTime

SELECT t1.xTime, t1.taskCount, t2.qualifiedCount, ROUND(t2.qualifiedCount / t1.taskCount,2) passRate FROM ( select count(1) taskCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm') xTime from MIDHD.MT_DETECT_TASK_INFO
IS_DEL = 0 AND TO_CHAR(DETECT_END_DATE,'yyyy/mm') BETWEEN to_char(#{dto.startDealTime},'yyyy/mm') AND to_char(#{dto.endDealTime},'yyyy/mm')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm') )t1 LEFT JOIN ( SELECT count(1) qualifiedCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm') xTime FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND DET_CONC_CODE = 1 AND TO_CHAR(DETECT_END_DATE,'yyyy/mm') BETWEEN to_char(#{dto.startDealTime},'yyyy/mm') AND to_char(#{dto.endDealTime},'yyyy/mm')
AND EQUIP_CATEG = #{dto.equipCateg}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
AND LINE_NO = #{dto.lineNo}
GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm') )t2 ON t1.xTime = t2.xTime

SELECT COUNT(1) taskCount, TO_CHAR(DETECT_END_DATE,'hh24') xTime FROM MIDHD.MT_DETECT_TASK_INFO mdti
IS_DEL = 0
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') = TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND mdti.DETECT_TASK_NO IN ( SELECT DISTINCT TASK_NO FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND DET_CONC_CODE = 1
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') = TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
) GROUP BY TO_CHAR(DETECT_END_DATE,'hh24')

SELECT COUNT(1) taskCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd') xTime FROM MIDHD.MT_DETECT_TASK_INFO mdti
IS_DEL = 0
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') between TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd') AND TO_CHAR(#{dto.endDealTime},'yyyy-mm-dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND mdti.DETECT_TASK_NO IN ( SELECT DISTINCT TASK_NO FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND DET_CONC_CODE = 1
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm-dd') between TO_CHAR(#{dto.startDealTime},'yyyy-mm-dd') AND TO_CHAR(#{dto.endDealTime},'yyyy-mm-dd')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
) GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm/dd')

SELECT COUNT(1) taskCount, TO_CHAR(DETECT_END_DATE,'yyyy/mm') xTime FROM MIDHD.MT_DETECT_TASK_INFO mdti
IS_DEL = 0
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm') between TO_CHAR(#{dto.startDealTime},'yyyy-mm') AND TO_CHAR(#{dto.endDealTime},'yyyy-mm')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND mdti.DETECT_TASK_NO IN ( SELECT DISTINCT TASK_NO FROM MIDHD.MT_POSITION_DETECT_INFO
IS_DEL = 0 AND DET_CONC_CODE = 1
AND TO_CHAR(DETECT_END_DATE,'yyyy-mm') between TO_CHAR(#{dto.startDealTime},'yyyy-mm') AND TO_CHAR(#{dto.endDealTime},'yyyy-mm')
AND EQUIP_CATEG = #{dto.equipCateg}
AND LINE_NO = #{dto.lineNo}
AND EQUIP_SPEC_CODE = #{dto.equipSpecCode}
) GROUP BY TO_CHAR(DETECT_END_DATE,'yyyy/mm')
